ion'),  'guestbook'		=> array('text'=>'Guestbook', 'url'=>'guestbook'),  'user'			=> array('text'=>'Användare', 'url'=>'user'),);/**	Session variable name***/$mvc->config['session_key']		= "MVC";$mvc->config['session_name'] = preg_replace('/[:\.\/-_]/', '', $_SERVER["SERVER_NAME"]);/**	CMUser-Admin*	name		- Admin name*	password	- Admin password***/$mvc->config['CMUser-Admin'] = array(	'name'			=> 'root',	'password'		=> 'root',	'email'			=> 'test@example.com',	'acronym'		=> 'root',);/**	CMUser-Groups*	Do not touch***/$mvc->config['CMUser-Groups'] = array(	'admin' => array(		'acronym'	=> 'admin',		'name'		=> 'The Administrator Group',	),	'user' => array(		'acronym'	=> 'user',		'name'		=> 'The User Group',	));