<?php/*	Site configuration file*/error_reporting(-1);ini_set('display_errors', 1);$mvc->config['session_name'] = preg_replace('/[:\.\/-_]/', '', $_SERVER["SERVER_NAME"]);/*** System settings**/$mvc->config['timezone'] = 'Europe/Stockholm';$mvc->config['character_encoding'] = 'iso-8859-1';$mvc->config['language'] = 'en';/** Available controllers**/$mvc->config['controllers'] = array('index' 		=> array('enabled' => true, 'class' => 'CCMe'),'developer' 	=> array('enabled' => true, 'class' => 'CCDeveloper'),'guestbook' 	=> array('enabled' => true, 'class' => 'CCGuestbook'),'presentation'	=> array('enabled' => true, 'class' => 'CCPresentation'),);/** Theme information**/$mvc->config['theme'] = array('name' => 'core',);/** Base url for website**/$mvc->config['base_url'] = null;/** Outgoing url-type:* * default		= 0 -> index.php/controller/method/arg1/arg2/arg3* clean			= 1 -> controller/method/arg1/arg2/arg3* q-string		= 2 -> index.php?q=controller/method/arg1/arg2/arg3*/$mvc->config['url_type'] = 1;/**	Database settings*	dsn			- Unavaliable*	host		- hostname*	user		- username for db*	password	- password for user*	db			- database to access*	dbDriver	- Driver for sql*		- available drivers:*			Mysqli***/$mvc->config['database'] = array(	'active'	=> false,	'dsn'		=> '',	'host'		=> '',	'user'		=> '',	'password'	=> '',	'db'		=> '',	'dbDriver'	=> 'Mysqli',);/**	Debug information messages*	false = don't show*	true = show**/$mvc->config['debugEnabled'] = true;$mvc->config['debug'] = array(	'mvc' => false,	'db-num-queries' => true,);// The contents of the kmoms navlinks$mvc->config['navkmom'] = array(  'kmom01'   => array('text'=>'kmom01',  'url'=>false),);// The contents of the navbar$mvc->config['navbar'] = array(  'index'         => array('text'=>'Me',  'url'=>'index'),  'presentation'   => array('text'=>'Redovisning',  'url'=>'presentation'),  'guestbook'    => array('text'