<?phpclass CCDeveloper extends CObject implements IController{	public function __construct()	{		parent::__construct();		$this->config['debugMode'] = 1;	}		public function Index()	{		$this->data['title'] = "The developer controller";		$this->data['main'] =<<<EOD		<a href="{$this->request->CreateUrl("developer/test")}">test method</a>EOD;	}		public function test($arg1="test")	{		$this->data['title'] = "Test!";		if ($arg1=="test")		{			$form = new CForm(array("action" => $this->request->CreateUrl("developer/test/test")),			array(				"hello"		=> new CFormElementText("hello"),				"Doh"		=> new CFormElementPassword("Doh"),				"Ok!"		=> new CFormElementSubmit("Ok!"))			);						$form->SetValidation("hello", array("numeric"));						$form->Check();						$this->data['main'] = $form->GetHTML().'<br /> Inne i testtest';		}		else		{					$form = new CForm(array("action" => $this->request->CreateUrl("developer/test")));						$form->AddElement(new CFormElementText("hello"));						$form->AddElement(new CFormElementPassword("Doh"));						$form->AddElement(new CFormElementSubmit("Ok!"));					$this->data['main'] = $form->GetHTML();		}	}		public function Links()	{		$this->request->cleanUrl = false;		$this->request->queryStringUrl = true;		$querystring = $this->request->CreateUrl("developer/DisplayObject/");				$this->data['title'] = "The Developer Controller";				$this->data['main'] =<<<EOD		<a href="{$querystring}">developer</a>EOD;	}		public function DisplayObject()	{		/*$this->Menu();*/				$this->data['main'] = <<<EOD		<h2>Dumping content of CDeveloper</h2>		<p>Dumping content of the controller, including properties from CObject which holds		access to common resources in CLydia</p>EOD;		$this->data['main'] .= '<pre>' . htmlent(print_r($this, true)) . '</pre>';	}}