<?phpclass CMGuestbook extends CObject{	private $db = null;	public function __construct()	{		parent::__construct();	}		public function Add($entry)	{		$this->database->Insert('posts', $entry);		$this->session->AddMessage('info', 'Message inserted successfully.');	}		public function ReadAll()	{		return $this->database->Get('posts', '', array(), array('time'), false);	}		public function DeleteAll()	{		$this->database->Delete('posts');		$this->session->AddMessage('success', 'Removed all messages from the database table.');	}}