<?phpclass CMGuestbook extends CObject{	private $db = null;	public function __construct()	{		parent::__construct();	}		public function Init()	{		$this->database->RunQuery("CREATE TABLE IF NOT EXISTS `posts` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `text` text NOT NULL,  `author` varchar(128) NOT NULL,  `time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,  PRIMARY KEY (`id`)) ENGINE=InnoDB  DEFAULT CHARSET=latin1;");	}		public function Add($entry)	{		$this->database->Insert('posts', $entry);		$this->session->AddMessage('info', 'Message inserted successfully.');	}		public function ReadAll()	{		return $this->database->Get('posts', '', array(), array('time'), false);	}		public function DeleteAll()	{		$this->database->Delete('posts');		$this->session->AddMessage('success', 'Removed all messages from the database table.');	}}