<?phpclass CCModules extends CObject implements IController{	public function __construct()	{		parent::__construct();	}		public function Index()	{		$modules		= new CMModules();		$controllers	= $modules->AvailableControllers();		$allModules		= $modules->ReadAndAnalyse();		$this->views->SetTitle('Manage Modules');		$this->views->AddView('Modules/index.tpl.php', array(			'controllers'	=> $controllers,			),			'primary'		);		$this->views->AddView('Modules/sidebar.tpl.php',array(			'modules'		=> $allModules,			),			'sidebar'		);	}}