<?php/*** Configuration-specifik form** @package NocturnalCore*/class CConfigureForm extends CForm implements ArrayAccess{	public function __construct($form = array(), $elements = array())	{		parent::__construct($form, $elements);	}		public function GetHTMLForElements() {    $html = null;    $buttonbar = null;	$navConf = false;	$navI = 0;    foreach($this->elements as $element) {	  if (isset($element))	  {		if (strpos($element['name'], 'Nav-')!==false && !$navConf)		{			$navI = 1;			$navConf = true;			$html .= '<table><tr><td>';		} else if ($navConf && strpos($element['name'], 'Nav-')===false)		{			$html .= "</td></tr></table>\n";			$navConf = false;		} else if ($navConf && strpos($element['name'], 'Nav-')!==false)		{			$html .= '</td>';			if ($navI == 3)			{				$html .= '</tr><tr>';				$navI = 0;			}			$html .= '<td>';			$navI++;		}		if(!$buttonbar && $element['type'] == 'submit') {          $buttonbar = true;          $html .= "<p>";        } else if($buttonbar && $element['type'] != 'submit') {          $buttonbar = false;          $html .= "</p>\n";        }		$html .= $element->GetHTML();	  }    }	if ($buttonbar)	{		$html .= "</p>\n";	}    return $html;  }}