<?php/*** Configuration-specifik form** @package NocturnalCore*/class CFormFrame extends CForm implements ArrayAccess{	public function __construct($form = array(), $elements = array())	{		parent::__construct($form, $elements);	}		public function GetHTMLForElements() {    $html = null;    $buttonbar = null;	$selectbar = false;    foreach($this->elements as $element) {	  if (isset($element))	  {		if (!$selectbar && $element['type'] == 'select')		{			$html .= "<table><tr><td>".$element->GetHTML()."</td>";			$selectbar = true;			$selectX = 0;			continue;		}		else if ($selectbar && $element['type'] != 'select')		{			$html .= "</tr></table>";			$selectX = 0;			$selectbar = false;		}		else if ($selectbar && $element['type']=='select')		{			if ($selectX==2)			{				$selectX = 0;				$html .= "</tr><tr>";			}			else			{				$selectX++;			}			$html .= "<td>".$element->GetHTML()."</td>";			continue;		}		if(!$buttonbar && $element['type'] == 'submit') {          $buttonbar = true;          $html .= "<p>";        } else if($buttonbar && $element['type'] != 'submit') {          $buttonbar = false;          $html .= "</p>\n";        }		$html .= $element->GetHTML();	  }    }	if ($buttonbar)	{		$html .= "</p>\n";	}	if ($selectbar)	{		$html .= "</tr></table>";	}    return $html;  }}