<? if(!$admin): ?><h2>You are not authorized to view this content.</h2><? else: ?><? if($users!=null): ?><a href="<?=create_controller_url('index')?>">ACP</a> > Users<? else: ?><a href="<?=create_controller_url('index')?>">ACP</a> > <a href="<?=create_controller_url('user')?>">Users</a> > <?=$user['id']?><? endif;?><h2>User manager</h2><? if($users!=null): ?><table><tr><?php$i = 0;foreach($users as $u){	if ($i==5)	{		$i = 0;		echo "</tr><tr>";	}		echo "<td><a href='".create_controller_url('user/'.$u['id'])."'>{$u['acronym']}</a></td>";		$i++;}?></tr></table><? else: ?><table class="smaller-text"><tr><td>Id:</td><td><?=$user['id']?></td></tr><tr><td>Acronym:</td><td><?=$user['acronym']?></td></tr><tr><td>Name:</td><td><?=$user['name']?></td></tr><tr><td>Email:</td><td><?=$user['email']?></td></tr><tr><td>Registered:</td><td><?=$user['created']?></td></tr><tr><td>In Groups:</td><td><? foreach($user['groups'] as $group): ?><?=$group['acronym']?>, <? endforeach; ?></td></tr></table>Add to group:<table><tr><?php$i = 0;foreach($groups as $group){	$cont = true;	foreach($user['groups'] as $ugroup)	{		if ($group['acronym'] == $ugroup['acronym'])		{			$cont = false;		}	}	if ($cont)	{		if ($i==3)		{			$i=0;			echo "</tr><tr>";		}				echo "<td>".create_button(create_controller_url("addgroup/{$user['id']}/{$group['id']}"), "<img src='".base_url('site/data/nocturnal/add.png')."' />".$group['acronym'])."</td>";				$i++;	}}?></tr></table>Remove from group:<table><tr><?php$i = 0;foreach($groups as $group){	$cont = true;	foreach($user['groups'] as $ugroup)	{		if ($group['acronym'] == $ugroup['acronym'])		{			$cont = false;		}	}	if (!$cont)	{		if ($i==3)		{			$i=0;			echo "</tr><tr>";		}				echo "<td>".create_button(create_controller_url("removegroup/{$user['id']}/{$group['id']}"), "<img src='".base_url('site/data/nocturnal/remove.png')."' />".$group['acronym'])."</td>";				$i++;	}}?></tr></table><? endif; ?><? endif; ?>