<?php if(!is_array($module)): ?><p>404. No such module.</p><?php else: ?><h2>Module: <?=$module['name']?></h2><h3>Description</h3><!-- <p>File: <code><?=$module['filename']?></code></p> --><p><?=nl2br($module['doccomment'])?></p><h3>Details</h3><table><caption>Details of module.</caption><thead><tr><th>Characteristics</th><th>Applies to module</th></tr></thead><tbody>  <tr><td>Part of Nocturnal Core modules</td><td><?=$module['isNocturnalCore']?'Yes':'No'?></td></tr>  <tr><td>Part of Nocturnal CMF modules</td><td><?=$module['isNocturnalCMF']?'Yes':'No'?></td></tr>  <tr><td>Part of Nocturnal Extra modules</td><td><?=$module['isNocturnalExtra']?'Yes':'No'?></td></tr>  <tr><td>Implements interface(s)</td><td><?=empty($module['interface'])?'No':implode(', ', $module['interface'])?></td></tr>  <tr><td>Controller</td><td><?=$module['isController']?'Yes':'No'?></td></tr>  <tr><td>Model</td><td><?=$module['isModel']?'Yes':'No'?></td></tr>  <tr><td>Manageable as a module</td><td><?=$module['isManageable']?'Yes':'No'?></td></tr>  <tr><td>Is a form</td><td><?=$module['isForm']?'Yes':'No'?></td></tr></tbody></table><?if(!empty($module['publicMethods'])): ?><h3>Public methods</h3><?php foreach($module['methods'] as $method): ?><?php if($method['isPublic']): ?><h3><?=$method['name']?></h3><p><?=nl2br($method['doccomment'])?></p><p>Implemented through lines: <?=$method['startline']?> - <?=$method['endline']?>.</p><?php endif; ?><?php endforeach; ?><?php endif; ?><?if(!empty($module['protectedMethods'])): ?><h3>Protected methods</h3><?php foreach($module['methods'] as $method): ?><?php if($method['isProtected']): ?><h3><?=$method['name']?></h3><p><?=nl2br($method['doccomment'])?></p><p>Implemented through lines: <?=$method['startline']?> - <?=$method['endline']?>.</p><?php endif; ?><?php endforeach; ?><?php endif; ?><?if(!empty($module['privateMethods'])): ?><h3>Private methods</h3><?php foreach($module['methods'] as $method): ?><?php if($method['isPrivate']): ?><h3><?=$method['name']?></h3><p><?=nl2br($method['doccomment'])?></p><p>Implemented through lines: <?=$method['startline']?> - <?=$method['endline']?>.</p><?php endif; ?><?php endforeach; ?><?php endif; ?><?if(!empty($module['staticMethods'])): ?><h3>Static methods</h3><?php foreach($module['methods'] as $method): ?><?php if($method['isStatic']): ?><h3><?=$method['name']?></h3><p><?=nl2br($method['doccomment'])?></p><p>Implemented through lines: <?=$method['startline']?> - <?=$method['endline']?>.</p><?php endif; ?><?php endforeach; ?><?php endif; ?><?php endif; ?>