<?php if($content['id']):?>  <h1><?=filter_data($content['title'], $content['filter'])?></h1>  <p><?=filter_data($content['data'], $content['filter'])?></p>  <p class='smaller-text silent'><a href='<?=create_url("content/edit/{$content['id']}")?>'>edit</a> <a href='<?=create_url("content")?>'>view all</a></p><?php else:?>  <h2>Page Controller</h2><span class="smaller-text"><?=create_button(create_url('content/create'), "<img src='".base_url('site/data/nocturnal/add.png')."' />Add Content")?><?php if($contents != null):?>  <table>  <tr><td>Actions:</td><td>Key:</td><td>By user:</td><td>Filter:</td><td>Created:</td><td>Active:</td></tr>  <?php foreach($contents as $val):?>    <tr>	<td>	<? if ($authorized): ?>	<?=create_button(create_url('content/edit/'.$val['id']),"<img src='".base_url('site/data/nocturnal/edit.png')."' />Edit")?>	<? endif; ?>	<?=create_button(create_url('page/view/'.$val['id']),"View")?>	</td>	<td><?=$val['key']?></td>	<td><?=$val['owner']?></td>	<td><?=$val['filter']?></td>	<td><?=$val['created']?></td>	<td><?=(empty($val['deleted'])?'<span class="active">Yes</span>':'<span class="inactive">No</span>')?></td>	</tr>  <?php endforeach; ?></table><?php else:?>  No content exists.<?php endif;?>  </span><?php if($authorized):?><?php endif; ?><?php endif;?>