<?php if($content['id']):?>  <h1><?=filter_data($content['title'], $content['filter'])?></h1>  <p><?=filter_data($content['data'], $content['filter'])?></p>  <p class='smaller-text silent'><a href='<?=create_url("content/edit/{$content['id']}")?>'>edit</a> <a href='<?=create_url("content")?>'>view all</a></p><?php else:?>  <h2>Page Controller Index</h2><h3>Pages</h3><?php if($contents != null):?>  <ul>  <?php foreach($contents as $val):?>    <li><p<?php if($val['deleted']!=null) echo ' class="strike"';?>><?=$val['id']?>, <?=$val['title']?> by <?=$val['owner']?><?php if($admin || $val['idUser']==$user['id']):?> <a href='<?=create_url("content/edit/{$val['id']}")?>'>edit</a><?php endif;?>&nbsp; &nbsp;<? if (!$val['deleted']):?><a href="<?=create_url($val['key'])?>">View</a><? endif; ?></p></li>  <?php endforeach; ?></ul><?php else:?>  <p>No content exists.</p><?php endif;?><?php if($admin):?><h4>Actions</h4><ul>  <li><a href='<?=create_url('content/create')?>'>Create new content</a></ul><?php endif; ?><?php endif;?>